mapper.delete:
delete from basket
where member_id = #{memberId}
and liq_id = #{liqId}

mapper.update:
update basket
set qty = #{qty},
amount = #{amount}
where member_id = #{memberId}
and liq_id = #{liqId}

mapper.select:
  	select * 
	from basket
	where member_id = #{memberId}
	and liq_id = #{liqId}
