mapper.select:

		select c.liq_id, c.nm, c.cate,c.regdate,c.updatedate,c.liq_thumb,c.liq_img,c.co_name
		from (select rownum rn,a.liq_id, a.nm, a.cate,a.regdate,a.updatedate,a.liq_thumb,a.liq_img, a.co_name
		from (select l.liq_id, l.nm, l.cate,l.regdate,l.updatedate,l.liq_thumb,l.liq_img,co.nm co_name
		from liq l, liq_co co
		where l.liq_co_id =co.liq_co_id
        order by liq_id desc)a
        where rownum<=#{pageNum}*#{amount}
        ) c
		where rn>(#{pageNum} -1)*#{amount}


select count(*) from liq 
where cate = #{cate}
and liq_id > 0

mapper.update:
update liq_cnt
set revw_cnt = revw_cnt + #{amount}
where liq_id = #{liqId}

update liq_cnt
set like_cnt = like_cnt + #{amount}
where liq_id = #{liqId}

update liq_cnt
set inqr_cnt = inqr_cnt + #{amount}
where liq_id = #{liqId}
