mapper.select:

		select c.liq_id, c.nm, c.cate,c.regdate,c.updatedate,c.liq_img
		from (select rownum rn,liq.liq_id, liq.nm, liq.cate,liq.regdate,liq.updatedate,liq.liq_img
		from (select liq_id, nm, cate,regdate,updatedate,liq_img
		from liq 
        order by liq_id desc) liq
        where rownum<=#{pageNum}*#{amount}
        ) c
		where rownum>(#{pageNum} -1)*#{amount}

mapper.update:
update liq_cnt
set revw_cnt = revw_cnt + #{amount}
where liq_id = #{liqId}

update liq_cnt
set like_cnt = like_cnt + #{amount}
where liq_id = #{liqId}

update liq_cnt
set inqr_cnt = inqr_cnt + #{amount}
where liq_id = #{liqId}
